import React from 'react';
import Navbar from './layouts/Navbar.component';
import 'bootstrap/dist/css/bootstrap.css';
import { Routes, Route } from 'react-router-dom';
import CreateAuthor from './components/CreateAuthor.component';
import EditBook from './components/EditBook.component';
import CreateBook from './components/CreateBook.component';
import Footer from './layouts/Footer';
import Login from './pages/Login';
import Home from './pages/Home';

function App() {
  return (
    <React.Fragment>
      <Navbar />

      <div>
        <Login />
      </div>
      <div className="container">
        <Routes>
          <Route path="/" exact element={<Home />} />
          <Route path="/edit/:id" element={<EditBook />} />
          <Route path="/create" element={<CreateBook />} />
          <Route path="/author" element={<CreateAuthor />} />
        </Routes>
      </div>
      <div>
        <Footer />
      </div>
    </React.Fragment>
  );
}

export default App;
