import React, { useEffect, useState } from 'react';
import ListItems from './ui/ListItems';
import Button from './ui/Button.component';
import { getAuthors, addBook } from '../lib/apiServices';
import { useNavigate } from 'react-router-dom';
import Select from './ui/Select';

const CreateBook = () => {
  const [book, setBook] = useState([]);
  const [selectedAuthors, setSelectedAuthors] = useState([]);
  const [ratin, setRating] = useState('5');

  const navigate = useNavigate();
  const formImputRef = React.useRef(null);
  const focusImput = () => {
    formImputRef.current.focus();
  };
  useEffect(() => {
    const handleAuthors = () => {
      getAuthors()
        .then((response) => {
          let res = response.data;

          const shortData = res.map((data) => ({
            name: data['name'],
          }));
          console.log(shortData);
          setBook({ authors: shortData });
        })
        .catch((error) => {
          console.error(error);
        });
    };
    handleAuthors();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    const bookData = {
      title: book.title,
      description: book.description,
      authors: book.selection,
      coverImageUrl: book.urlImage,
    };
    console.log(bookData);

    addBook(bookData)
      .then((res) => {
        console.log(res.data);
        navigate('/');
      })
      .catch((err) => console.log(err));
  };

  const handleOnchange = (e) => {
    const { name, value } = e.target;
    setBook((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSelectChange = (option) => {
    setBook((prevData) => ({ ...prevData, authors: option }));
  };

  function setItems(item) {
    setSelectedAuthors([...selectedAuthors, item]);
  }

  const { title, description, urlImage, rating, authors, selection } = book;
  return (
    <React.Fragment>
      <div>
        <form onSubmit={handleSubmit}>
          <h2>Create a book</h2>
          <div className="form-group">
            <label className="form-label">Title</label>
            <input
              ref={formImputRef}
              className="form-control"
              type="text"
              name="title"
              value={title}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea
              className="form-control"
              type="text"
              name="description"
              value={description}
              onChange={(e) => setRating(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label className="form-label">CoverUrlImage</label>
            <input
              className="form-control"
              type="text"
              name="urlImage"
              value={urlImage}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">rating:{ratin}</label>
            <input
              className="form-control"
              type="range"
              min="0"
              max="5"
              name="rating"
              value={rating}
              onChange={handleOnchange}
            />
          </div>
          <div>
            <Select
              items={book.authors}
              labelItems="Authors"
              setItems={setItems}
            />
          </div>

          <div className="form-group">
            <Button type="submit" className="btn btn-primary" value="Submit">
              Submit
            </Button>
          </div>
        </form>
        <div>{selectedAuthors}</div>
      </div>
    </React.Fragment>
  );
};

export default CreateBook;
