import React, { Component } from 'react';

export class CreateBook extends Component {
  constructor() {
    super();

    this.inputRef = React.createRef();

    this.state = {
      title: '',
      description: '',
      urlImage: '',
      ranking: 0,
    };
  }
  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
  };
  handleOnchange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const { title, description, urlImage, ranking } = this.state;
    return (
      <React.Fragment>
        <div>
          <form onSubmit={this.handleSubmit}>
            <h2>Create a book</h2>
            <div className="form-group">
              <label className="form-label">Title</label>
              <input
                className="form-control"
                type="text"
                name="title"
                value={title}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                className="form-control"
                type="text"
                name="description"
                value={description}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">CoverUrlImage</label>
              <input
                className="form-control"
                type="text"
                name="urlImage"
                value={urlImage}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Ranking</label>
              <input
                className="form-control"
                type="number"
                name="ranking"
                value={ranking}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Author</label>
              <select className="form-control">
                <option value="mat"> Mat</option>
                <option value="isa"> Izabel</option>
                <option value="fraha"> Fraha</option>
              </select>
            </div>
            <div className="form-group">
              <Button type="submit" className="btn btn-primary" value="Submit">
                Submit
              </Button>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default CreateBook;
