import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ListItems from './ui/ListItems';
import Button from './ui/Button.component';
import { getAuthors, addBook } from '../lib/apiServices';
import { withRouter } from 'react-router-dom';

const CreateBook = () => {
  const [book, setBook] = useState([]);

  useEffect(() => {
    getAuthors()
      .then((response) => {
        this.setState({ authors: response.data });
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  handleSubmit = (e) => {
    e.preventDefault();
    const getData = (data) => {
      console.log(data);
    };
    const bookData = {
      title: this.state.title,
      description: this.state.description,
      authors: this.state.selection,
      coverImageUrl: this.state.urlImage,
    };
    console.log(bookData);

    addBook(bookData)
      .then((res) => {
        console.log(res.data);
        this.props.history.push('/');
      })
      .catch((err) => console.log(err));
  };

  handleOnchange = (e) => {
    setBook({ [e.target.name]: e.target.value });
  };

  const { title, description, urlImage, ranking, authors } = this.state;
  return (
    <React.Fragment>
      <div>
        <form onSubmit={this.handleSubmit}>
          <h2>Create a book</h2>
          <div className="form-group">
            <label className="form-label">Title</label>
            <input
              className="form-control"
              type="text"
              name="title"
              value={title}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea
              className="form-control"
              type="text"
              name="description"
              value={description}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">CoverUrlImage</label>
            <input
              className="form-control"
              type="text"
              name="urlImage"
              value={urlImage}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Ranking</label>
            <input
              className="form-control"
              type="number"
              name="ranking"
              value={ranking}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <ListItems
              items={authors}
              labelItems="Authors"
              onSelect={() => this.selection}
            ></ListItems>
          </div>
          <div className="form-group">
            <Button type="submit" className="btn btn-primary" value="Submit">
              Submit
            </Button>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
};

export default CreateBook;
