import axios from 'axios';

export const getAuthors = () => {
  return axios.get('http://localhost:5000/authors');
};

export const addAuthor = (author) => {
  return axios.post('http://localhost:5000/authors/add', author);
};

export const getBooks = () => {
  axios.get('http://localhost:5000/books').then((res) => {
    return res.data;
  });
};

export const addBook = (author) => {
  return axios.post('http://localhost:5000/books/add', author);
};
