import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './layouts/Navbar.component';
import 'bootstrap/dist/css/bootstrap.css';
import CreateAuthor from './components/CreateAuthor.component';
import EditBook from './components/EditBook.component';
import BookList from './components/BookList.component';
import CreateBook from './components/CreateBook.component';

function App() {
  return (
    <Router>
      {' '}
      {/* Wrap your Routes component with Router */}
      <>
        <Navbar />
        <br />
        <div className="container">
          <Routes>
            <Route path="/" element={<BookList />} />
            <Route path="/edit/:id" element={<EditBook />} />
            <Route path="/create" element={<CreateBook />} />
            <Route path="/author" element={<CreateAuthor />} />
          </Routes>
        </div>
      </>
    </Router>
  );
}

export default App;
