import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Book({ book }) {
  const [id, setId] = useState('');
  const onError = () => {
    console.log('Image no charged');
  };
  const handleClick = (item) => {
    setId(id);
    console.log(item.title);
  };
  return (
    <div className="col-md-4">
      <div className="row g-0 rounded overflow-hidden flex-md-row mb-4 shadow-sm">
        <div className="col p-4 d-flex flex-column ">
          <Link to={`/details`} book={book}>
            <img src={book.coverImageUrl} onError={onError}></img>
          </Link>
          <a href="/detail/${}" onClick={() => handleClick(book)}></a>
        </div>
        <div className="col-auto d-none d-lg-block">
          <strong className=" mb-2 text-success">{book.title}</strong>
          <small className="mb-1 ">Year..</small>
        </div>
      </div>
    </div>
  );
}

export default Book;
