import React from 'react';
import { useState } from 'react';

function Book({ book }) {
  const handleClick = () => {
    console.log('hello');
  };
  return (
    <div className="col-md-4">
      <div className="row g-0 border rounded overflow-hidden flex-md-row mb-4 shadow-sm">
        <div
          className="col p-4 d-flex flex-column "
          onClick={() => handleClick(book._id)}
        >
          <strong className=" mb-2 text-success">{book.title}</strong>
          <small className="mb-1 text-body-secondary">Year..</small>
        </div>
        <div className="col-auto d-none d-lg-block">
          <img className="bd-placeholder-img" src="@book.CoverImageUrl" />
        </div>
      </div>
    </div>
  );
}

export default Book;
