import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ListItems from './ui/ListItems';
import Button from './ui/Button.component';
import { getAuthors, addBook } from '../lib/apiServices';
import { useNavigate } from 'react-router-dom';

const CreateBook = () => {
  const [book, setBook] = useState([]);

  useEffect(() => {
    getAuthors()
      .then((response) => {
        setBook({ authors: response.data });
        console.log(book);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const getData = (data) => {
      console.log(data);
    };
    const bookData = {
      title: book.title,
      description: book.description,
      authors: book.selection,
      coverImageUrl: book.urlImage,
    };
    console.log(bookData);

    addBook(bookData)
      .then((res) => {
        console.log(res.data);
        navigate('/');
      })
      .catch((err) => console.log(err));
  };

  const handleOnchange = (e) => {
    const { name, value } = e.target;
    setBook((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSelectChange = (option) => {
    setBook((prevData) => ({ ...prevData, authors: option }));
  };

  const { title, description, urlImage, ranking, authors, selection } = book;
  return (
    <React.Fragment>
      <div>
        <form onSubmit={handleSubmit}>
          <h2>Create a book</h2>
          <div className="form-group">
            <label className="form-label">Title</label>
            <input
              className="form-control"
              type="text"
              name="title"
              value={title}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Description</label>
            <textarea
              className="form-control"
              type="text"
              name="description"
              value={description}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">CoverUrlImage</label>
            <input
              className="form-control"
              type="text"
              name="urlImage"
              value={urlImage}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <label className="form-label">Ranking</label>
            <input
              className="form-control"
              type="number"
              name="ranking"
              value={ranking}
              onChange={handleOnchange}
            />
          </div>
          <div className="form-group">
            <ListItems
              items={book.authors}
              labelItems="Authors"
              onSelect={() => selection}
            ></ListItems>
          </div>
          <div className="form-group">
            <Button type="submit" className="btn btn-primary" value="Submit">
              Submit
            </Button>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
};

export default CreateBook;
