import React, { Component } from 'react';
import axios from 'axios';
import ListItems from './ui/ListItems';
import Button from './ui/Button.component';
import { getAuthors, addAuthor } from '../lib/apiServices';
import { useHistory } from 'react-router-dom';

export class CreateBook extends Component {
  constructor() {
    super();

    this.inputRef = React.createRef();

    this.state = {
      title: '',
      description: '',
      urlImage: '',
      ranking: 0,
      authors: [],
    };
  }

  componentDidMount() {
    getAuthors()
      .then((result) => {
        this.setState({ authors: result.data });
      })
      .catch((err) => console.log('try again'));
  }

  /*   handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const Author = {
      title: this.state.title,
      description: this.state.description,
      authors: this.state.authors,
      coverImageUrl: this.state.urlImage,
    };

      const response = await axios.post('http://localhosto:5000/books/add', {
        Author,
      });
      this.props.history.push('/');
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  }; */

  handleSubmit = (e) => {
    e.preventDefault();
    const Author = {
      title: this.state.title,
      description: this.state.description,
      authors: this.state.authors,
      coverImageUrl: this.state.urlImage,
    };
    console.log(Author);
    const response = axios
      .post('http://localhosto:5000/authors/add', Author)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => console.log(err));
    console.log('estate', this.state);
  };
  handleOnchange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { title, description, urlImage, ranking, authors } = this.state;
    return (
      <React.Fragment>
        <div>
          <form onSubmit={this.handleSubmit}>
            <h2>Create a book</h2>
            <div className="form-group">
              <label className="form-label">Title</label>
              <input
                className="form-control"
                type="text"
                name="title"
                value={title}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                className="form-control"
                type="text"
                name="description"
                value={description}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">CoverUrlImage</label>
              <input
                className="form-control"
                type="text"
                name="urlImage"
                value={urlImage}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Ranking</label>
              <input
                className="form-control"
                type="number"
                name="ranking"
                value={ranking}
                onChange={this.handleOnchange}
              />
            </div>
            <div className="form-group">
              <ListItems items={authors} labelItems="Authors"></ListItems>
            </div>
            <div className="form-group">
              <Button type="submit" className="btn btn-primary" value="Submit">
                Submit
              </Button>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default CreateBook;
